<?php\n/**\n * The template for displaying the blog posts index\n *\n * @link https://developer.wordpress.org/themes/basics/template-hierarchy/\n *\n * @package sacco-php\n */\n\nget_header();\n?>\n\n<main id=\"primary\" class=\"site-main\">\n\n\t<section class=\"page-header bg-light py-5\">\n\t\t<div class=\"container\">\n\t\t\t<div class=\"row\">\n\t\t\t\t<div class=\"col-md-12 text-center\">\n\t\t\t\t\t<h1 class=\"page-title\"><?php single_post_title(); ?></h1>\n\t\t\t\t\t<?php\n\t\t\t\t\tif ( function_exists(\'yoast_breadcrumb\') ) {\n\t\t\t\t\t\tyoast_breadcrumb( \'<p id=\"breadcrumbs\" class=\"breadcrumbs\">\',\'</p>\' );\n\t\t\t\t\t} else {\n\t\t\t\t\t\t// Basic breadcrumbs fallback\n\t\t\t\t\t\t?>\n\t\t\t\t\t\t<div class=\"breadcrumbs\">\n\t\t\t\t\t\t\t<a href=\"<?php echo esc_url(home_url(\'/\')); ?>\"><?php esc_html_e(\'Home\', \'sacco-php\'); ?></a> &gt; \n\t\t\t\t\t\t\t<?php single_post_title(); ?>\n\t\t\t\t\t\t</div>\n\t\t\t\t\t\t<?php\n\t\t\t\t\t}\n\t\t\t\t\t?>\n\t\t\t\t</div>\n\t\t\t</div>\n\t\t</div>\n\t</section>\n\n\t<section class=\"blog-index-section py-5\">\n\t\t<div class=\"container\">\n\t\t\t<div class=\"row\">\n\t\t\t\t<div class=\"col-lg-8\">\n\t\t\t\t\t<?php if ( have_posts() ) : ?>\n\t\t\t\t\t\t<div class=\"blog-posts-grid\">\n\t\t\t\t\t\t\t<?php\n\t\t\t\t\t\t\t/* Start the Loop */\n\t\t\t\t\t\t\twhile ( have_posts() ) :\n\t\t\t\t\t\t\t\tthe_post();\n\t\t\t\t\t\t\t\t/*\n\t\t\t\t\t\t\t\t * Include the Post-Format-specific template for the content.\n\t\t\t\t\t\t\t\t * If you want to override this in a child theme, then include a file\n\t\t\t\t\t\t\t\t * called content-___.php (where ___ is the Post Format) and that will be used instead.\n\t\t\t\t\t\t\t\t */\n\t\t\t\t\t\t\t\tget_template_part( 'template-parts/content', get_post_type() ? get_post_type() : get_post_format() );\n\t\t\t\t\t\t\tendwhile;\n\t\t\t\t\t\t\t?>\n\t\t\t\t\t\t</div>\n\n\t\t\t\t\t\t<div class=\"pagination-container mt-5\">\n\t\t\t\t\t\t\t<?php sacco_php_pagination(); ?>\n\t\t\t\t\t\t</div>\n\n\t\t\t\t\t<?php else : ?>\n\t\t\t\t\t\t<?php get_template_part( 'template-parts/content', 'none' ); ?>\n\t\t\t\t\t<?php endif; ?>\n\t\t\t\t</div>\n\t\t\t\t<div class=\"col-lg-4\">\n\t\t\t\t\t<?php get_sidebar(); ?>\n\t\t\t\t</div>\n\t\t\t</div>\n\t\t</div>\n\t</section>\n\n</main><!-- #main -->\n\n<?php\nget_footer(); 